import Layout from '../components/Layout';

class SSRTest extends React.component {
    static async getInitialProps ({req}) {
        return req ? {from: 'server'} : {from: 'client'}
    }

    render() {
        return(
            <Layout>
                {this.props.from} is running
            </Layout>
        );
    };
}

export default SSRTest;
