import React, { Component } from "react";
import { Layout } from "antd";
import Typewriter from "typewriter-effect";
import styled from "styled-components";

const Title = styled.h2`
	 {
		display: block;
		margin: 0 auto;
		-webkit-box-pack: center;
		-webkit-justify-content: center;
		-ms-flex-pack: center;
		justify-content: center;
		-webkit-box-align: center;
		-webkit-align-items: center;
		-ms-flex-align: center;
		align-items: center;
		-webkit-box-flex: 0;
		-webkit-flex: 0 auto;
		-ms-flex: 0 auto;
		flex: 0 auto;
		font-size: 68px;
		line-height: 60px;
		font-weight: 300;
		text-align: center;
		color: #fff;
		font-family: "Montserrat";
	}
`;

const Section = styled.div`
	 {
		display: flex;
		position: absolute;
		margin-top: 72px;
		left: 0%;
		top: 0%;
		right: 0%;
		bottom: auto;
		z-index: 2;
		min-height: 80vh;
		align-items: center;
		display: flex;
		padding-right: 32px;
		padding-left: 32px;
		-webkit-box-align: center;
		background-image: linear-gradient(180deg, #000, rgba(0, 0, 0, 0.89) 61%, transparent);
	}
`;

export default class Hero extends Component {
	render() {
		return (
			<Section className="section-hero">
				<Title>
					<span>Discover Local</span>
					<Typewriter
						onInit={typewriter => {
							typewriter
								.typeString("Lessons")
								.pauseFor(1500)
								.deleteAll()
								.typeString("Classes")
								.pauseFor(1500)
								.deleteAll()
								.typeString("Events")
								.start();
						}}
					/>
					<span>Today!</span>
				</Title>
			</Section>
		);
	}
}
